import { useState } from "react";


const CakeForm = ({info, setInfo}) => {

    const [cakeName, setCakeName] = useState("");
    const [cakeIngredient, setCakeIngredient] = useState([]);
    const [cakePrice, setCakePrice] = useState(0);
    const [cakeRating, setCakeRating] = useState(0);



    const handleFormSubmit = (e) => {
        e.preventDefault();
        console.log("Form submitted");}


    return (  
        <>
        <h3>Complete form to add a new cake!!</h3>
        <form onSubmit={handleFormSubmit}>
            <input 
            type="text"
            name="cake name"
            placeholder="cake name"
            value={cakeName}
            onChange={(e) => setCakeName(e.target.value)}

            />

            <input 
            type="text"
            name="ingredients"
            placeholder="ingredients"
            value={cakeIngredient}
            onChange={(e) => setCakeIngredient(e.target.value)}
            />

            <input 
            type="text"
            name="price"
            placeholder="price"
            value={cakePrice}
            onChange={(e) => setCakePrice(e.target.value)}
            />

            <input 
            type="text"
            name="rating"
            placeholder="cake rating"
            value={cakeRating}
            onChange={(e) => setCakeRating(e.target.value)}
            />
            <input 
            type="submit"
            value="Submit"/>
        </form>
        </>

    );
}

export default CakeForm;
